from pyflink.table import ScalarFunction
import requests
import os
import json

class GetLocation(ScalarFunction):
    def eval(self, ip_address: str) -> str:
        api_key = os.environ.get('IP2LOCATION_API_KEY')
        if not api_key:
            return json.dumps({})
        url = f'https://ip2location.io/api/{api_key}?ip={ip_address}'
        try:
            response = requests.get(url, timeout=10)
            if response.status_code == 200:
                data = response.json()
                country = data.get('country', '')
                state = data.get('region', '')
                city = data.get('city', '')
                return json.dumps({'country': country, 'state': state, 'city': city})
            else:
                return json.dumps({})
        except Exception:
            return json.dumps({})

import requests
import os

def gentle_request(wav_path, txt_path, server_addr, port, debug=False):
    if not os.path.exists(wav_path) or not os.path.exists(txt_path):
        return None
    wav_base = os.path.splitext(os.path.basename(wav_path))[0]
    txt_base = os.path.splitext(os.path.basename(txt_path))[0]
    if wav_base != txt_base:
        return None
    url = f'http://{server_addr}:{port}'
    try:
        with open(wav_path, 'rb') as wav_file, open(txt_path, 'rb') as txt_file:
            response = requests.post(url, files={'wav_file': wav_file, 'txt_file': txt_file})
        if response.status_code == 200:
            if debug:
                print(response.json())
            return response
        return None
    except Exception as e:
        return None

