import requests
from PIL import Image
import io
import logging

class TelegramClient:
    def __init__(self, chat_id, thread_id=None, bot_token):
        self.chat_id = chat_id
        self.thread_id = thread_id
        self.bot_token = bot_token
        self.logger = logging.getLogger(__name__)

    def send_message(self, message, disable_links_preview=True):
        url = f"https://api.telegram.org/bot{self.bot_token}/sendMessage"
        data = {
            'chat_id': self.chat_id,
            'thread_id': self.thread_id,
            'text': message,
            'parse_mode': 'Markdown',
            'disable_web_page_preview': disable_links_preview
        }
        response = requests.post(url, data=data)
        if response.status_code != 200:
            self.logger.error(f"Failed to send message: {response.text}")
        else:
            self.logger.info("Message sent successfully")

    def send_file(self, file_name, contents):
        try:
            img = Image.open(io.BytesIO(contents))
            if img.format == 'SVG':
                png_data = io.BytesIO()
                img.save(png_data, format='PNG')
                contents = png_data.getvalue()
                file_name = file_name.replace('.svg', '.png')
        except Exception as e:
            self.logger.warning(f"Could not convert SVG to PNG: {e}")

        is_photo = False
        try:
            img = Image.open(io.BytesIO(contents))
            is_photo = True
        except:
            pass

        if is_photo:
            url = f"https://api.telegram.org/bot{self.bot_token}/sendPhoto"
            files = {'photo': io.BytesIO(contents)}
            params = {
                'chat_id': self.chat_id,
                'thread_id': self.thread_id,
                'caption': file_name
            }
        else:
            url = f"https://api.telegram.org/bot{self.bot_token}/sendDocument"
            files = {'document': io.BytesIO(contents)}
            params = {
                'chat_id': self.chat_id,
                'thread_id': self.thread_id,
                'filename': file_name
            }

        response = requests.post(url, params=params, files=files)
        if response.status_code != 200:
            self.logger.error(f"Failed to send file: {response.text}")
        else:
            self.logger.info("File sent successfully")

