import requests
import logging
from enum import Enum
from typing import Tuple, Dict, Any, Union, Optional

class SXTApiCallTypes(Enum):
    GET = 'GET'
    POST = 'POST'
    PUT = 'PUT'
    DELETE = 'DELETE'

class SXTBaseAPI:
    APICALLTYPE = SXTApiCallTypes

    def __init__(self, access_token: str = '', logger: logging.Logger = None):
        self.api_url = 'https://api.spaceandtime.app'
        self.access_token = access_token
        self.logger = logger or logging.getLogger(__name__)
        self.network_calls_enabled = True
        self.standard_headers = {'Content-Type': 'application/json'}
        self.versions = {}

    def call_api(self, endpoint: str, auth_header: bool = True, request_type: str = SXTApiCallTypes.POST, 
                 header_parms: Dict[str, Any] = {}, data_parms: Dict[str, Any] = {},
                 query_parms: Dict[str, Any] = {}, path_parms: Dict[str, Any] = {}) -> Tuple[bool, Dict[str, Any]]:
        original_endpoint = endpoint
        processed_endpoint = endpoint
        for key, value in path_parms.items():
            processed_endpoint = processed_endpoint.replace(f'{{{key}}}', str(value))

        url = self.api_url
        if original_endpoint in self.versions:
            url += '/' + self.versions[original_endpoint]
        url += '/' + processed_endpoint

        headers = self.standard_headers.copy()
        if auth_header:
            headers['Authorization'] = self.access_token
        headers.update(header_parms)

        if not self.network_calls_enabled:
            return self.__fakedata__(endpoint)
        else:
            try:
                if request_type == SXTApiCallTypes.GET:
                    response = requests.get(url, params=query_parms, headers=headers)
                elif request_type == SXTApiCallTypes.POST:
                    response = requests.post(url, json=data_parms, headers=headers, params=query_parms)
                elif request_type == SXTApiCallTypes.PUT:
                    response = requests.put(url, json=data_parms, headers=headers, params=query_parms)
                elif request_type == SXTApiCallTypes.DELETE:
                    response = requests.delete(url, headers=headers, params=query_parms)
                else:
                    raise ValueError(f"Unsupported request type: {request_type}")
            except Exception as e:
                self.logger.error(f"Network error: {str(e)}")
                return (False, {})

            if response.status_code >= 200 and response.status_code < 300:
                self.logger.info(f"Successfully called {url} with status {response.status_code}")
                return (True, response.json())
            else:
                self.logger.error(f"API call failed: {url}, status code {response.status_code}, response: {response.text}")
                return (False, response.json())

    def __fakedata__(self, endpoint: str) -> Union[list, dict]:
        if endpoint.startswith('/users'):
            return {
                "users": [
                    {"id": 1, "name": "Alice", "email": "alice@example.com"},
