app = Flask(__name__)

def get_predictions(text, bing_key=None):
    return [f"Prediction for {text}"] * 10

@app.route('/invocations', methods=['POST'])
def transformation():
    content_type = request.headers.get('Content-Type')
    if content_type == 'application/json':
        data = request.get_json()
        text = data['text']
        bing_key = data.get('bing_key')
        predictions = get_predictions(text, bing_key)
        top_10 = predictions[:10]
        return Response(json.dumps({'predictions': top_10}), mimetype='application/json', status=200)
    elif content_type in ['text/csv', 'text/plain']:
        if content_type == 'text/csv':
            csv_data = request.data.decode('utf-8')
            import csv
            reader = csv.DictReader(io.StringIO(csv_data))
            texts = [row['text'] for row in reader]
        else:
            texts = request.data.decode('utf-8').splitlines()
        predictions = [get_predictions(text) for text in texts]
        output = io.StringIO()
        writer = csv.writer(output)
        for pred in predictions:
            writer.writerow([pred])
        csv_output = output.getvalue()
        return Response(csv_output, mimetype='text/csv', status=200)
    else:
        return Response("Unsupported content type", status=415, mimetype='text/plain')

