from flask import Flask, request, Response, jsonify
import base64
import json
import datetime
import logging

app = Flask(__name__)

def process_circleci_event(headers, msg):
    event_type = headers.get('Circleci-Event-Type')
    if event_type not in ['push', 'pull_request', 'deployment', 'schedule']:
        logging.warning(f"Unsupported event type: {event_type}")
        return None
    try:
        event_data = json.loads(msg['data'])
    except json.JSONDecodeError:
        logging.error("Failed to parse event data")
        return None
    metadata_str = json.dumps(event_data)
    time_created = datetime.datetime.utcnow().isoformat()
    signature = headers.get('Circleci-Signature')
    return {
        "event_type": event_type,
        "id": msg['message_id'],
        "metadata": metadata_str,
        "time_created": time_created,
        "signature": signature,
        "msg_id": msg['message_id'],
        "source": "circleci"
    }

@app.route('/', methods=['POST'])
def index():
    try:
        data = request.get_json()
        if not data or 'message' not in data:
            return jsonify({"error": "Invalid request"}), 400
        message = data['message']
        if 'attributes' not in message or 'data' not in message or 'message_id' not in message:
            return jsonify({"error": "Missing required fields in message"}), 400
        attributes = message['attributes']
        headers_str = attributes.get('headers')
        if not headers_str:
            return jsonify({"error": "Headers missing"}), 400
        headers = json.loads(headers_str)
        msg_data = message['data']
        msg_id = message['message_id']
        event_type = headers.get('Circleci-Event-Type')
        signature = headers.get('Circleci-Signature')
        if not event_type or not signature:
            return jsonify({"error": "Missing required headers"}), 400
        try:
            decoded_data = base64.b64decode(msg_data).decode('utf-8')
        except Exception as e:
            logging.error(f"Error decoding data: {e}")
            return jsonify({"error": "Invalid data"}), 400
        event_info = process_circleci_event(headers, {'data': decoded_data, 'message_id': msg_id})
        if not event_info:
            logging.warning(f"Unsupported event type: {event_type}")
            return Response(status=204)
        return Response(status=204)
    except Exception as e:
        logging.error(f"Error processing request: {e}")
        return jsonify({"error": "Internal server error"}), 500

if __name__ == '__main__':
    app.run(host='0.0.0.0', port=5000)

