                                             url_prefix='/swagger',
                                             template_folder='templates',
                                             static_folder='static')
        self.app.register_blueprint(swagger_blueprint)
        self.app.add_url_rule('/spec', 'get_spec', self.get_spec, methods=['GET'])

    def _setup_routes(self):
        @self.app.route('/users/<int:user_id>', methods=['GET'])
        def get_user(user_id: int):
            return self.get_user(user_id)

        @self.app.route('/users', methods=['GET'])
        def list_users():
            return self.list_users()

        @self.app.route('/users', methods=['POST'])
        def create_user():
            return self.create_user()

        @self.app.route('/users/<int:user_id>', methods=['PUT'])
        def update_user(user_id: int):
            return self.update_user(user_id)

        @self.app.route('/users/<int:user_id>', methods=['DELETE'])
        def delete_user(user_id: int):
            return self.delete_user(user_id)

        @self.app.route('/orders/<string:order_id>', methods=['DELETE'])
        def delete_order(order_id: str):
            return self.delete_order(order_id)

    def get_spec(self):
        return self.app.blueprints['swagger_ui'].get_spec()

    def get_user(self, user_id: int) -> Tuple[Dict[str, Any], int]:
        user = self.users.get(user_id)
        if not user:
            if self.config.simulate_404_on_missing_user:
                return jsonify({'error': 'User not found'}), 404
            else:
                return jsonify({'error': 'Invalid user ID'}), 400
        return jsonify(user), 200

    def list_users(self) -> Tuple[Dict[str, Any], int]:
        return jsonify([user for user in self.users.values()]), 200

    def create_user(self) -> Tuple[Dict[str, Any], int]:
        data = request.get_json()
        if not data or 'name' not in data or 'email' not in data:
            return jsonify({'error': 'Missing required fields'}), 400
        user = {
            'id': self.next_user_id,
            'name': data['name'],
            'email': data['email'],
            'created_at': datetime.datetime.now().isoformat(),
            'updated_at': datetime.datetime.now().isoformat()
        }
        self.users[user['id']] = user
        self.next_user_id += 1
        return jsonify(user), 201

    def update_user(self, user_id: int) -> Tuple[Dict[str, Any], int]:
        data = request.get_json()
        user = self.users.get(user_id)
        if not user:
            return jsonify({'error': 'User not found'}), 404
        user.update({
            'name': data.get('name', user['name']),
            'email': data.get('email', user['email']),
            'updated_at': datetime.datetime.now().isoformat()
        })
        return jsonify(user), 200

    def delete_user(self, user_id: int) -> Tuple[Dict[str, Any], int]:
        user = self.users.pop(user_id, None)
        if user:
            self.deleted_orders.add(str(user_id))
            return jsonify({'message': 'User deleted'}), 200
        return jsonify({'error': 'User not found'}), 404

    def delete_order(self, order_id: str) -> Tuple[Dict[str, Any], int]:
        self.deleted_orders.add(order_id)
        return jsonify({'message': 'Order deleted'}), 200

    def check_auth(self):
        auth_header = request.headers.get('Authorization')
        if not auth_header:
            return jsonify({'error': 'Unauthorized'}), 401
        # Add more authentication checks if needed
        return None
