        else:
            SECRET_KEY = 'devkey, should be in a file'
        self.app.config['SECRET_KEY'] = SECRET_KEY

    def _setup_routes(self):
        @self.app.before_request
        def before_request():
            g.db = self.connect_db()
            g.user = None
            if 'user_id' in session:
                g.user = self.query_db('select * from user where user_id = ?',
                                    [session['user_id']], one=True)

        @self.app.teardown_request
        def teardown_request(exception):
            db = getattr(g, 'db', None)
            if db is not None:
                db.close()

        @self.app.route("/")
        def intmain():
            vstr = request.args.get('vfilter', 'all')
            papers = [db[pid] for pid in DATE_SORTED_PIDS]
            papers = self.papers_filter_version(papers, vstr)
            ctx = self.default_context(papers, render_format='recent',
                                    msg='Showing most recent Arxiv papers:')
            return render_template('main.html', **ctx)

        @self.app.route('/login', methods=['POST'])
        def login():
            if not request.form['username']:
                flash('You have to enter a username')
            elif not request.form['password']:
                flash('You have to enter a password')
            elif self.get_user_id(request.form['username']) is not None:
                user = self.query_db('''select * from user where
                    username = ?''', [request.form['username']], one=True)
                if check_password_hash(user['pw_hash'], request.form['password']):
                    session['user_id'] = self.get_user_id(request.form['username'])
                    flash('User ' + request.form['username'] + ' logged in.')
                else:
                    flash('User ' + request.form['username'] + ' already exists, wrong password.')
            else:
                creation_time = int(time.time())
                g.db.execute('''insert into user (username, pw_hash, creation_time) values (?, ?, ?)''',
                    [request.form['username'], 
                    generate_password_hash(request.form['password']), 
                    creation_time])
                user_id = g.db.execute('select last_insert_rowid()').fetchall()[0][0]
                g.db.commit()
                session['user_id'] = user_id
                flash('New account %s created' % (request.form['username'], ))
            return redirect(url_for('intmain'))

    def connect_db(self):
        sqlite_db = sqlite3.connect(Config.database_path)
        sqlite_db.row_factory = sqlite3.Row
        return sqlite_db

    def query_db(self, query, args=(), one=False):
        cur = g.db.execute(query, args)
        rv = cur.fetchall()
        return (rv[0] if rv else None) if one else rv

