from waitress import serve

class FlaskAPI:
    def __init__(self):
        self.app = Flask(__name__)
        self.server_thread = None
        self.app.config['flask_api'] = self
        self._setup_routes()

    def _setup_routes(self):
        @self.app.route('/status')
        def status():
            return jsonify({'status': 'running'})

        @self.app.route('/input', methods=['POST'])
        def input_handler():
            data = request.json
            text = data.get('text')
            screen = data.get('screen')
            talk = data.get('talk')
            return jsonify(_the_input(text, screen, talk))

        @self.app.route('/stop', methods=['POST'])
        def stop_server():
            self.app.config['flask_api'].stop_api()
            return jsonify({'status': 'stopped'})

    def _the_input(self, text: str, screen: str, talk: str) -> dict:
        return {'response': f'Processed text: {text}, screen: {screen}, talk: {talk}'}

    def start_api(self, api: bool = False):
        if api:
            serve(self.app, host='0.0.0.0', port=5000)
        else:
            self.server_thread = ServerThread(self.app, '0.0.0.0', 5000)
            self.server_thread.start()

    def stop_api(self):
        if self.server_thread:
            self.server_thread.shutdown()
            self.server_thread.join()

class ServerThread(threading.Thread):
    def __init__(self, app: Flask, host: str, port: int):
        super().__init__()
        self.app = app
        self.host = host
        self.port = port
        self.srv = None
        self.ctx = None

    def run(self):
        self.ctx = self.app.app_context()
        self.ctx.push()
        self.srv = make_server(self.host, self.port, self.app)
        self.srv.serve_forever()

    def shutdown(self):
        if self.srv:
            self.srv.shutdown()
        if self.ctx:
            self.ctx.pop()

