import sys
import threading
from flask import Flask, render_template, jsonify
import webbrowser

def trace(files=None, port=9999, host='127.0.0.1', browser=False, daemon=False):
    execution_data = {}

    def tracer(frame, event, arg):
        if files is not None and not files(frame.filename):
            return
        if event == 'line':
            filename = frame.filename
            lineno = frame.f_lineno
            if filename not in execution_data:
                execution_data[filename] = {}
            if lineno not in execution_data[filename]:
                execution_data[filename][lineno] = 0
            execution_data[filename][lineno] += 1
        return tracer

    sys.settrace(tracer)

    app = Flask(__name__)

    @app.route('/')
    def index():
        return render_template('index.html')

    @app.route('/data')
    def get_data():
        return jsonify(execution_data)

    thread = threading.Thread(target=app.run, kwargs={'host': host, 'port': port})
    if daemon:
        thread.daemon = True
    thread.start()

    if browser:
        webbrowser.open(f'http://{host}:{port}')

    while True:
        pass

