sessions = {}

def generate_qr_data(url):
    qr = qrcode.make(url)
    img_byte_arr = BytesIO()
    qr.save(img_byte_arr, format='PNG')
    img_byte_arr.seek(0)
    encoded = base64.b64encode(img_byte_arr.read()).decode('utf-8')
    return encoded

@app.route('/get_qrcode', methods=['GET'])
def get_qrcode():
    url = 'https://example.com'
    qr_data = generate_qr_data(url)
    sid = str(uuid.uuid4())
    sessions[sid] = {'qr_url': qr_data, 'status': 'generated'}
    return jsonify({'qr_url': qr_data, 'session_id': sid})

@app.route('/check_qrcode/<sid>', methods=['GET'])
def check_qrcode(sid):
    if sid in sessions:
        return jsonify({'status': sessions[sid]['status']})
    else:
        return jsonify({'error': 'Session not found'})

