from flask import Flask, request, jsonify, Response, Blueprint
from flask_cors import CORS
import logging
import sys
import time

class Server:
    def __init__(self, data=None, formats=None, authorization=None, allow_profiler=False, profiler_output=None, profile_by_default=False, allow_add=False, logfile=sys.stdout, loglevel='WARNING', log_exception_formatter=None):
        self.app = Flask(__name__)
        self.data = data or {}
        self.formats = formats or ['json']
        self.logfile = logfile
        self.loglevel = loglevel
        self.setup_logging()
        CORS(self.app)
        self.api = Blueprint('api', __name__)
        self.setup_api_routes()
        self.app.register_blueprint(self.api)

    def setup_logging(self):
        logging.basicConfig(
            filename=self.logfile,
            level=self.loglevel,
            format='%(asctime)s - %(levelname)s - %(message)s'
        )

    def setup_api_routes(self):
        @self.api.route('/compserver', methods=['POST', 'HEAD', 'OPTIONS'])
        def compserver():
            try:
                if request.method == 'POST':
                    content_type = request.headers.get('Content-Type', '')
                    if content_type in self.formats:
                        if content_type == 'application/json':
                            data = request.get_json()
                        elif content_type == 'application/xml':
                            data = request.data
                        else:
                            data = request.get_json()
                        result = {'status': 'success', 'data': data}
                        return jsonify(result)
                    else:
                        return jsonify({'error': 'Unsupported format'}), 406
                elif request.method == 'HEAD':
                    return Response(status=200)
                elif request.method == 'OPTIONS':
                    return Response(status=204)
            except Exception as e:
                logging.error(f"Error in compserver: {str(e)}")
                return jsonify({'error': 'Internal server error'}), 500

    def run(self, port=6363, retry=False, **kwargs):
        while True:
            try:
                self.app.run(port=port, **kwargs)
                break
            except Exception as e:
                if retry:
                    logging.warning(f"Port {port} is busy. Retrying...")
                    time.sleep(1)
                    port += 1
                else:
                    logging.error(f"Failed to start server on port {port}: {str(e)}")
                    raise

