        self.app.route('/api/stop/<sid>')(self.api_stop)

    def _start_janitor(self):
        threading.Thread(target=self.janitor, daemon=True).start()

    def janitor(self, sessions):
        while True:
            time.sleep(60)
            now = time.time()
            for session in sessions:
                if 'timestamp' in session and now - session['timestamp'] > 300:
                    sessions.remove(session)

    def root(self):
        return "DNSTwist API", 200

    def api_scan(self):
        sid = f"session_{int(time.time())}"
        session = {
            'sid': sid,
            'status': 'running',
            'results': [],
            'timestamp': time.time()
        }
        self.sessions.append(session)
        # Simulate domain permutation and scanning
