            generator = dnstwist.DNSTwist()
            permutations = generator.permute("example.com")
            for domain in permutations:
                result = dnstwist.check(domain)
                session['results'].append(result)
            session['status'] = 'completed'
        except Exception as e:
            session['status'] = 'failed'
            session['error'] = str(e)
        return f"Scan session {sid} completed", 200

    def api_status(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return "Session not found", 404
        return jsonify(session), 200

