            writer.writerow([domain['domain'], domain['status'], domain['registrar']])
        csv_data = output.getvalue()
        return Response(csv_data, mimetype='text/csv', headers={'Content-Disposition': f'attachment; filename=domains_{sid}.csv'}), 200

    def api_json(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return "Session not found", 404
        return jsonify(session['results']), 200

    def api_list(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return "Session not found", 404
        return "\n".join([f"{d['domain']} - {d['status']}" for d in session['results']]), 200

    def api_stop(self, sid):
        session = next((s for s in self.sessions if s['sid'] == sid), None)
        if not session:
            return "Session not found", 404
        session['status'] = 'stopped'
        return "Session stopped", 200

    def run(self, host='0.0.0.0', port=5000):
        self.app.run(host=host, port=port)

