        self.app = Sanic("ExchangeRateAPI")
        self.db = Gino()
        self.config = {
            "DATABASE_URL": os.environ.get("DATABASE_URL")
        }
        self.db.init_app(self.app)
        self._setup_routes()

    async def _setup_routes(self):
        @self.app.route("/exchange-rates", methods=["GET"])
        async def exchange_rates(request):
            date = request.args.get("date")
            base = request.args.get("base")
            symbols = request.args.get("symbols").split(",") if request.args.get("symbols") else None

            # Determine the date to use
            if not date:
                latest_date = await self.db.gino.select(
                    self.db.func.max(ExchangeRates.date).select()
                ).gino.scalar()
                date = latest_date
            else:
                try:
                    date_obj = datetime.strptime(date, "%Y-%m-%d")
                except ValueError:
                    return json({"error": "Invalid date format"}, status=400)

            # Query the exchange rates for the date
            rates_data = await ExchangeRates.query.where(ExchangeRates.date == date).gino.first()

            if not rates_data:
                return json({"error": "No exchange rates found for the specified date"}, status=404)

            # Check if the base is present in the rates
