class SanicHttpClient:
    def __init__(self, graphiql: Optional[bool], graphql_ide: Optional[str], allow_queries_via_get: bool, result_override: Optional[Any], multipart_uploads_enabled: bool) -> None:
        self.app = sanic.Sanic()
        self.graphiql = graphiql
        self.graphql_ide = graphql_ide or "graphiql"
        self.allow_queries_via_get = allow_queries_via_get
        self.result_override = result_override
        self.multipart_uploads_enabled = multipart_uploads_enabled
        self._setup_routes()

    def _setup_routes(self):
        @self.app.route("/graphql", methods=["POST"])
        async def graphql(request: Request):
            if self.graphiql:
                return sanic.Response("GraphiQL", status=200)
            try:
                data = request.json
                query = data.get("query")
                variables = data.get("variables")
                if not query:
                    return sanic.Response("Missing query", status=400)
                if self.result_override:
                    return sanic.Response(json.dumps({"data": self.result_override}), status=200)
                return sanic.Response(json.dumps({"data": "GraphQL response"}), status=200)
            except Exception as e:
                return sanic.Response(str(e), status=500)

    def _graphql_request(self, method: str, query: Optional[str], variables: Optional[Dict[str, object]], files: Optional[Dict[str, BytesIO]], headers: Optional[Dict[str, str]], **kwargs: Any) -> Response:
        if method == "post":
            body = {}
            if query:
                body["query"] = query
            if variables:
                body["variables"] = variables
            if files and self.multipart_uploads_enabled:
                body = files
            return self._send_request(method, body, headers, **kwargs)
        elif method == "get":
            if query and self.allow_queries_via_get:
                return self._send_request(method, query, headers, **kwargs)
        return self._send_request(method, "", headers, **kwargs)

    def _send_request(self, method: str, body: Optional[Dict], headers: Optional[Dict], **kwargs: Any) -> Response:
        client = Client(self.app)
        if method == "get":
            return client.get("", headers=headers, **kwargs)
        elif method == "post":
            return client.post("", json=body, headers=headers, **kwargs)
        elif method == "patch":
            return client.patch("", json=body, headers=headers, **kwargs)
        elif method == "put":
            return client.put("", json=body, headers=headers, **kwargs)
        elif method == "delete":
            return client.delete("", headers=headers, **kwargs)
        return Response()

    def request(self, url: str, method: str, headers: Optional[Dict] = None) -> Response:
        return self._send_request(method, "", headers)

    def get(self, url: str, headers: Optional[Dict] = None) -> Response:
        return self._send_request("get", "", headers)

    def post(self, url: str, data: Optional[bytes], json: Optional[Dict], headers: Optional[Dict] = None) -> Response:
        body = json if json else data
        return self._send_request("post", body, headers)

