        self.inference_engine_lda = InferenceEngineWrapper(self.model_dir, 'lda.conf', self.emb_file)
        self.inference_engine_slda = InferenceEngineWrapper(self.model_dir, 'slda.conf')
        self.twe = TopicalWordEmbeddingsWrapper(self.model_dir, self.emb_file)
