from kafka import KafkaProducer
from kafka.admin import AdminClient, NewTopic

def kafka_producer(servers, topic, input_path, timeout):
    logging.basicConfig(level=logging.INFO)
    logging.info("Starting Kafka producer...")

    admin_client = AdminClient({"bootstrap_servers": servers})

    try:
        topic_description = admin_client.describe_topics([topic]).result()
        if topic in topic_description:
            logging.info(f"Topic {topic} already exists.")
        else:
            new_topic = NewTopic(name=topic, num_partitions=1, replication_factor=1)
            futures = admin_client.create_topics([new_topic])
            for future in futures:
                try:
                    future.result(timeout=timeout)
                    logging.info(f"Topic {topic} created successfully.")
                except Exception as e:
                    logging.error(f"Failed to create topic {topic}: {e}")
    except Exception as e:
        logging.error(f"Error checking topic {topic}: {e}")

    admin_client.close()

    producer = KafkaProducer(
        bootstrap_servers=servers,
        request_timeout_ms=timeout * 1000
    )

    logging.info(f"Reading messages from {input_path}...")
    with open(input_path, 'r') as f:
        for i, line in enumerate(f):
            line = line.strip()
            if line:
                logging.info(f"Sending message {i+1}: {line}")
                producer.send(topic, line.encode('utf-8'))

    producer.close()
    logging.info("Kafka producer closed.")

