import threading
from kafka import KafkaProducer, RecordAccumulator, Sender, ClusterMetadata, Metrics
from kafka.serializer import StringSerializer, IntegerSerializer
from kafka.message import Message
from kafka.future import FutureRecordMetadata
import time
import atexit

class KafkaProducer:
    def __init__(self, **configs):
        self.config = configs
        self._metrics = Metrics()
        self._accumulator = RecordAccumulator()
        self._metadata = ClusterMetadata()
        self._sender = Sender()
        self._closed = False
        self._cleanup = self._cleanup_factory()
        atexit.register(self._unregister_cleanup)

    def send(self, topic, value=None, key=None, headers=None, partition=None, timestamp_ms=None):
        future = FutureRecordMetadata()
        self._accumulator.add(topic, value, key, headers, partition, timestamp_ms)
        return future

    def flush(self, timeout=None):
        self._accumulator.flush(timeout)

    def close(self, timeout=None):
        self._sender.stop()
        self._accumulator.close()
        self._closed = True

    def _cleanup_factory(self):
        def cleanup():
            if not self._closed:
                self.close()
        return cleanup

    def _unregister_cleanup(self):
        atexit.unregister(self._cleanup)

    def _max_usable_produce_magic(self):
        return 2

    def _estimate_size_in_bytes(self, key, value, headers=[]):
        return len(key) + len(value) + len(headers)

    def _ensure_valid_record_size(self, size):
        if size > 1048576:
            raise ValueError("Message size exceeds maximum allowed size")

    def _wait_on_metadata(self, topic, max_wait):
        start_time = time.time()
        while not self._metadata.has_topic(topic):
            if time.time() - start_time > max_wait:
                raise Exception("Timeout waiting for metadata")
            time.sleep(0.1)

    def _serialize(self, f, topic, data):
        if f == 'key':
            return StringSerializer()(topic)
        else:
            return StringSerializer()(data)

    def _partition(self, topic, partition, key, value, serialized_key, serialized_value):
        if partition is not None:
            return partition
        return hash(serialized_key + serialized_value) % self._metadata.partitions_for_topic(topic)

