from kafka import KafkaProducer, KafkaAdminClient

def test_kafka_message_delivery(kafka_broker, compression):
    allowed_compressions = {'none', 'gzip', 'snappy', 'lz4', 'zstd'}
    if compression not in allowed_compressions:
        pytest.skip(f"Compression type {compression} is not supported.")

    admin_client = KafkaAdminClient(bootstrap_servers=kafka_broker.host + ":" + str(kafka_broker.port))
    clusters = admin_client.describe_clusters()
    if not clusters:
        pytest.skip("No clusters found.")
    version = clusters[0].version

    if compression == 'lz4':
        if version < '2.4':
            pytest.skip("LZ4 compression requires Kafka 2.4+")
    elif compression == 'zstd':
        if version < '3.0':
            pytest.skip("Zstd compression requires Kafka 3.0+")

    producer = KafkaProducer(bootstrap_servers=kafka_broker.host + ":" + str(kafka_broker.port),
                             compression_type=compression)

    topic = 'test_topic'
    num_messages = 5
    messages = [f"message {i}" for i in range(num_messages)]
    futures = [producer.send(topic, msg) for msg in messages]

    for future in futures:
        future.get(timeout=10)

    assert len(futures) == len([future for future in futures if future.successful()])

    producer.close()

