import paho.mqtt.client as mqtt
import time
import ssl

def mqtt_publisher(host, topic, qos, clientid, username=None, disable_clean_session=False, password=None, port=None, nummsgs=1, delay=1.0, keepalive=60, use_tls=False, insecure=False, cacerts=None, tls_version=None, debug=False):
    def on_connect(client, userdata, flags, rc):
        if rc == 0:
            print("Connected successfully")
        else:
            print(f"Connection failed with code {rc}")

    def on_publish(client, userdata, mid):
        print(f"Message published with mid {mid}")

    ssl_context = None
    if use_tls:
        ssl_context = ssl.create_default_context()
        if insecure:
            ssl_context.verify_mode = ssl.CERT_NONE
        if cacerts:
            ssl_context.load_verify_locations(cafile=cacerts)
        if tls_version:
            if tls_version == "tlsv1":
                ssl_context.protocol = ssl.PROTOCOL_TLSv1
            elif tls_version == "tlsv1.1":
                ssl_context.protocol = ssl.PROTOCOL_TLSv1_1
            elif tls_version == "tlsv1.2":
                ssl_context.protocol = ssl.PROTOCOL_TLSv1_2
        else:
            ssl_context.protocol = ssl.PROTOCOL_TLSv1_2

    port = port if port is not None else (8883 if use_tls else 1883)

    client = mqtt.Client(clientid, clean_session=not disable_clean_session)
    client.on_connect = on_connect
    client.on_publish = on_publish
    if username and password:
        client.username_pw_set(username, password)
    if debug:
        client.debug = True

    if use_tls:
        client.connect(host, port, keepalive, ssl_context=ssl_context)
    else:
        client.connect(host, port, keepalive)

    client.loop_start()

