class BaseProxy:
    def __init__(self, service_url=None, service_port=None, btc_conf_file=None, timeout=DEFAULT_HTTP_TIMEOUT, connection=None):
        self.__url = urllib.parse.urlparse(service_url)
        self.__service_url = service_url
        self.__id_count = 0
        self.__auth_header = None
        self.__conn = connection or self._create_connection()
        if btc_conf_file:
            with open(btc_conf_file, 'r') as f:
                config = json.load(f)
            self.username = config.get('username')
            self.password = config.get('password')
            auth_userpass = f"{self.username}:{self.password}"
            self.__auth_header = base64.b64encode(auth_userpass.encode())

    def _create_connection(self):
        if self.__url.scheme == 'http':
            return http.client.HTTPConnection(self.__url.hostname, self.__url.port)
        else:
            return http.client.HTTPSConnection(self.__url.hostname, self.__url.port)

    def _call(self, service_name, *args):
        self.__id_count += 1
        id = self.__id_count
        request_body = {
            "jsonrpc": "2.0",
            "method": service_name,
            "params": list(args),
            "id": id
        }
        request_json = json.dumps(request_body)
        headers = {
            'Content-Type': 'application/json',
            'Authorization': self.__auth_header.decode('utf-8') if self.__auth_header else None
        }
        self.__conn.request('POST', self.__url.path, request_json, headers)
        response = self._get_response()
        return response

    def _get_response(self):
        response = self.__conn.getresponse()
        if response.status != 200:
            error_msg = f"HTTP error {response.status}: {response.reason}"
            raise Exception(error_msg)
        response_body = response.read().decode('utf-8')
        try:
            data = json.loads(response_body)
        except json.JSONDecodeError as e:
            raise Exception("Failed to decode JSON response") from e
        if data.get('jsonrpc') != '2.0':
            raise Exception("Invalid JSON-RPC response")
        if 'error' in data:
            raise Exception(data['error']['message'])
        return data

    def close(self):
        if self.__conn:
            self.__conn.close()

    def __del__(self):
        self.close()

