def translate_text(text: str, sourceLang: Optional[str] = None, targetLang: Optional[str] = "EN", numberAlternative: Optional[int] = 0, printResult: bool = False, proxies: Optional[dict] = None):
    api_key = os.environ.get('DEEPL_API_KEY')
    if not api_key:
        raise ValueError("DEEPL_API_KEY environment variable is not set")

    url = "https://api.deepl.com/v2/translate"
    headers = {
        "Authorization": f"DeepL-Auth-Key {api_key}"
    }
    data = {
        "text": text,
        "target_lang": targetLang,
        "source_lang": sourceLang,
        "alternatives": numberAlternative > 0
    }

    with httpx.Client(proxies=proxies) as client:
        response = client.post(url, headers=headers, json=data)

    if response.status_code == 429:
        raise TooManyRequestsException("Too many requests")
    elif response.status_code != 200:
        return None

    try:
        result = response.json()
    except ValueError:
        return None

    translations = result.get("translations", [])
    if not translations:
        return None

    if numberAlternative == 0:
        result_text = translations[0]["text"]
    else:
        result_text = [t["text"] for t in translations[1:][:numberAlternative]]

    if printResult:
        if isinstance(result_text, list):
            for t in result_text:
                print(t)
        else:
            print(result_text)

    return result_text

