            else:
                self._interface_address = '0.0.0.0'
            self._listen_socket.bind((self._interface_address, self._listen_port))
            self._listen_socket.listen(5)
            self._listen_socket.setblocking(False)
            self._selector.register(self._listen_socket, selectors.EVENT_READ, data=None)
            return True
        except Exception as e:
            print(f"Error binding listen port: {e}")
            return False

    def _accept_incoming_peer_connections(self):
        try:
            while not self.pending_shutdown:
                if self._listen_socket is not None:
                    try:
                        conn, addr = self._listen_socket.accept()
                        conn.setblocking(False)
                        self._conns[addr] = conn
                        self._selector.register(conn, selectors.EVENT_READ, data=addr)
                        self._num_sockets += 1
                    except OSError:
                        pass
                time.sleep(0.1)
        except Exception as e:
            print(f"Error accepting incoming connections: {e}")

    def run(self):
        if not self._create_listen_socket():
            return
        if not self._bind_listen_port():
            return
        self._selector.register(self._listen_socket, selectors.EVENT_READ, data=None)
        while not self.pending_shutdown:
            try:
                events = self._selector.select(timeout=1.0)
                for key, mask in events:
                    sock = key.fileobj
                    data = key.data
                    if sock == self._listen_socket:
                        self._accept_incoming_peer_connections()
                    else:
                        if mask & selectors.EVENT_READ:
                            self._process_ready_input_socket(sock, data)
                        elif mask & selectors.EVENT_WRITE:
                            self._process_ready_output_socket(sock, data)
            except Exception as e:
                print(f"Error in run loop: {e}")
        self._close_all_connections()

    def _process_ready_sockets(self, current_time):
