        try:
            conn = self._conns[data]
            data_received = sock.recv(4096)
            if data_received:
                self._read_data(conn, time.time())
        except Exception as e:
            print(f"Error processing input socket: {e}")
            self._close_connection(conn)

    def _process_ready_output_socket(self, sock, data):
        try:
            conn = self._conns[data]
            if self._write_data(conn, time.time()):
                self._modify_connection_events(conn, selectors.EVENT_READ)
        except Exception as e:
            print(f"Error processing output socket: {e}")
            self._close_connection(conn)

    def _read_data(self, conn, current_time):
        try:
            data = conn.read()
            if data:
                self._handle_message(data, conn, current_time)
        except Exception as e:
            print(f"Error reading data: {e}")
            self._close_connection(conn)

    def _write_data(self, conn, current_time):
        try:
            if conn.write_buffer:
                sent = conn.send(conn.write_buffer)
                conn.write_buffer = conn.write_buffer[sent:]
                if not conn.write_buffer:
                    self._modify_connection_events(conn, selectors.EVENT_READ)
                return True
            return False
        except Exception as e:
            print(f"Error writing data: {e}")
            return False

    def _modify_connection_events(self, conn, io_events):
        try:
            self._selector.modify(conn, io_events, data=conn)
        except Exception as e:
            print(f"Error modifying connection events: {e}")

    def _close_connection(self, conn):
        try:
            if conn in self._conns:
                del self._conns[conn]
            self._selector.unregister(conn)
            self._close_socket(conn)
        except Exception as e:
            print(f"Error closing connection: {e}")

    def _close_socket(self, sock):
        try:
            sock.close()
        except Exception as e:
            print(f"Error closing socket: {e}")

    def _close_all_connections(self):
        for conn in list(self._conns.values()):
            self._close_connection(conn)
        self._selector.close()

