        self.channel = {}

    def main_loop(self) -> None:
        while True:
            readable, _, _ = select.select(self.input_list, [], [])
            for sock in readable:
                if sock is self._server:
                    self.on_accept()
                else:
                    self.on_recv(sock)
            time.sleep(self.DELAY)

    def on_accept(self) -> None:
        client_socket, addr = self._server.accept()
        self.input_list.append(client_socket)
        self.channel[client_socket] = self._rdev

    def on_close(self, sock) -> None:
        if sock in self.input_list:
            self.input_list.remove(sock)
        if sock in self.channel:
            del self.channel[sock]
        for client, dev in self.channel.items():
            if dev == sock:
                if client in self.input_list:
                    self.input_list.remove(client)
                del self.channel[client]
                break

    def on_recv(self, sock) -> None:
        data = sock.recv(self.BUFFER_SIZE)
        if not data:
            self.on_close(sock)
            return
        if sock in self.channel:
            dev_sock = self.channel[sock]
            dev_sock.send(data)
        else:
            for client, dev_sock in self.channel.items():
                if dev_sock == sock:
                    client.send(data)
                    break
