class TCPRequestHandler(socketserver.BaseRequestHandler):
    def get_data(self):
        size_bytes = self.request.recv(2)
        if not size_bytes:
            return None
        size = int.from_bytes(size_bytes, byteorder='big')
        if size < 0:
            return None
        data = self.request.recv(size)
        if len(data) < size:
            return None
        return data

