from email.utils import formatdate

SSDP_PORT = 1900
SSDP_ADDR = '239.255.255.250'
SERVER_ID = 'SSDP Server'

class SSDPServer:
    def __init__(self):
        self.ip_list = []
        self.sock_list = []
        self.sock = None
        self.running = False
        self.ssdp_thread = None
        self.sending_byebye = True
        self.known = {}

    def start(self):
        if not self.running:
            self.running = True
            self.sending_byebye = True
            self.ssdp_thread = threading.Thread(target=self.run, name="SSDP_THREAD")
            self.ssdp_thread.start()

    def stop(self, byebye=True):
        if self.running:
            self.running = False
            try:
                socket.socket(socket.AF_INET, socket.SOCK_DGRAM).sendto(b'', (SSDP_ADDR, SSDP_PORT))
            except Exception:
                pass
            self.sending_byebye = byebye
            if self.ssdp_thread is not None:
                self.ssdp_thread.join()

    def run(self):
        self.sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
        self.sock.setsockopt(socket.IPPROTO_IP, socket.IP_MULTICAST_LOOP, 0)

        if sys.platform == 'win32':
            self.sock.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        elif sys.platform == 'darwin':
            self.sock.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEPORT, 1)
        elif hasattr(socket, "SO_REUSEPORT"):
            try:
                self.sock.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEPORT, 1)
            except socket.error:
                try:
                    self.sock.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
                except socket.error:
                    pass

        self.ip_list = [('192.168.137.1', '255.255.255.0')] if sys.platform == 'win32' else []
        self.sock_list = []
        for ip, mask in self.ip_list:
            try:
                mreq = socket.inet_aton(SSDP_ADDR) + socket.inet_aton(ip)
                self.sock.setsockopt(socket.IPPROTO_IP, socket.IP_ADD_MEMBERSHIP, mreq)
                self.sock_list.append(Sock(ip))
            except Exception:
                pass

        try:
            self.sock.bind(('0.0.0.0', SSDP_PORT))
        except Exception:
            return
        self.sock.settimeout(1)

        while self.running:
            try:
                data, addr = self.sock.recvfrom(1024)
                self.datagram_received(data, addr)
            except socket.timeout:
                continue

        self.shutdown()
        for ip, mask in self.ip_list:
            mreq = socket.inet_aton(SSDP_ADDR) + socket.inet_aton(ip)
            try:
                self.sock.setsockopt(socket.IPPROTO_IP, socket.IP_DROP_MEMBERSHIP, mreq)
            except Exception:
                continue
        self.sock.close()
        self.sock = None

    def datagram_received(self, data, host_port):
        try:
            header = data.decode().split('\r\n\r\n')[0]
        except ValueError:
            return
        if len(header) == 0:
            return

        lines = header.split('\r\n')
        cmd = lines[0].split(' ')
        lines = map(lambda x: x.replace(': ', ':', 1), lines[1:])
        lines = filter(lambda x: len(x) > 0, lines)
        headers = dict(map(lambda x: (x[0].lower(), x[1]), [x.split(':', 1) for x in lines]))

        if cmd[0] == 'M-SEARCH' and cmd[1] == '*':
            self.discovery_request(headers, host_port)

    def discovery_request(self, headers, host_port):
        (host, port) = host_port
        for i in self.known.values():
            if i['ST'] == headers['st'] or headers['st'] == 'ssdp:all':
                response = ['HTTP/1.1 200 OK']
                usn = None
                for k, v in i.items():
                    if k == 'USN':
                        usn = v
                    response.append('%s: %s' % (k, v))

                if usn:
                    response.append('DATE: %s' % formatdate(timeval=None, localtime=False, usegmt=True))
                    response.extend(('', ''))
                    destination = (host, port)
                    for ip, mask in self.ip_list:
                        if self.get_subnet_ip(ip, mask) == self.get_subnet_ip(host, mask):
                            self.sock.sendto('\r\n'.join(response).format(ip).encode(), destination)
                            break

    def get_subnet_ip(self, ip, mask):
        a = [int(n) for n in mask.split('.')]
        b = [int(n) for n in ip.split('.')]
        return [a[i] & b[i] for i in range(4)]

    def shutdown(self):
        for st in self.known:
            self.do_byebye(st)
        usn = [st for st in self.known]
        for st in usn:
            self.unregister(st)

    def register(self, usn, st, location, server=SERVER_ID, cache_control='max-age=1800'):
        self.known[usn] = {
