        async with httpx.AsyncClient() as client:
            try:
                response = await client.post(
                    f"{self.claude_base_url}/v1/messages",
                    json=claude_params,
                    headers={"Authorization": f"Bearer {self.get_api_key({})}"}
                )
                if response.is_streaming:
                    async for chunk in response.aiter_bytes():
                        yield self.claude_to_chatgpt_response_stream(chunk)
                else:
                    yield self.claude_to_chatgpt_response(response.json())
            except Exception as e:
                yield {"error": str(e)}

    def openai_to_claude_params(self, openai_params: Dict) -> Dict:
        return {
            "model": openai_params.get("model"),
            "prompt": openai_params.get("prompt"),
            "max_tokens": openai_params.get("max_tokens"),
            "temperature": openai_params.get("temperature")
        }

