import websockets
import logging

logger = logging.getLogger(__name__)

class DataStream:
    def __init__(self, endpoint: str, key_id: str, secret_key: str, raw_data: bool = False, websocket_params: Optional[Dict] = None):
        self._endpoint = endpoint
        self._key_id = key_id
        self._secret_key = secret_key
        self._ws = None
        self._running = False
        self._loop = asyncio.get_event_loop()
        self._raw_data = raw_data
        self._stop_stream_queue = queue.Queue()
        self._handlers: Dict[str, Dict] = {}
        self._name = "DataStream"
        self._should_run = True
        self._max_frame_size = 1024 * 1024  # 1MB
        self._websocket_params = websocket_params or {}

    async def _connect(self):
        self._ws = await websockets.connect(self._endpoint, **self._websocket_params)
        self._ws.max_frame_size = self._max_frame_size

    async def _auth(self):
        auth_payload = {
            "type": "auth",
            "key_id": self._key_id,
            "secret_key": self._secret_key
        }
        await self._ws.send(json.dumps(auth_payload))

    async def _start_ws(self):
        await self._connect()
        await self._auth()
        await self._consume()

    async def close(self):
        if self._ws:
            await self._ws.close()
            self._ws = None
            self._running = False

    def stop_ws(self):
        self._stop_stream_queue.put_nowait(True)

    async def _consume(self):
        while self._should_run:
            try:
                message = await self._ws.recv()
                msg = json.loads(message)
                await self._dispatch(msg)
            except websockets.exceptions.ConnectionClosed:
                break
            except Exception as e:
                logger.error(f"Error processing message: {e}")
                break

    async def _dispatch(self, msg: Dict):
        if msg.get("type") == "message":
            data_type = msg.get("data_type")
            if data_type in self._handlers:
                handler = self._handlers[data_type]
                if callable(handler):
                    await handler(msg)

    def _subscribe(self, handler: Callable, symbols: List[str], handlers: Dict):
        data_type = handlers["type"]
        self._handlers[data_type] = handler
        return self._subscribe_all()

    def _subscribe_all(self):
        for data_type, handler in self._handlers.items():
            symbols = [sym for sym in handler.get("symbols", [])]
            if symbols:
                self._subscribe_handler(data_type, symbols, handler)

    def _subscribe_handler(self, data_type: str, symbols: List[str], handler: Callable):
        subscribe_payload = {
            "type": "subscribe",
            "data_type": data_type,
            "symbols": symbols
        }
        self._ws.send(json.dumps(subscribe_payload))

    async def _run_forever(self):
        try:
            await self._start_ws()
        finally:
            self._running = False

    def run_forever(self):
        asyncio.ensure_future(self._run_forever())

    def stop(self):
        self._should_run = False
        self.stop_ws()
        self._loop.run_until_complete(self._run_forever())
        self._loop.close()

