            print(f"Received control message: {message}")
        data = json.loads(message)
        if data.get('type') == 'response':
            with self.lock:
                if data['id'] in self.pending_requests:
                    self.pending_requests[data['id']]['response'] = data['value']
                    del self.pending_requests[data['id']]

    def on_data_message(self, ws, message):
        if self.debug_mode:
            print(f"Received data message: {message}")

    def on_error(self, ws, error):
        if self.debug_mode:
            print(f"Error occurred: {error}")
        self.connection_established.clear()
        self.is_running = False

    def on_close(self, ws, close_status_code, close_msg):
        if self.debug_mode:
            print(f"Connection closed: {close_status_code} - {close_msg}")
        self.connection_established.clear()
        self.is_running = False

