            self.pending_requests[request_id] = {'type': 'get', 'parameter': parameter}
            message = {
                'type': 'request',
                'id': request_id,
                'command': 'get',
                'parameter': parameter
            }
            self.control_ws.send(json.dumps(message))
            while request_id not in self.pending_requests or self.pending_requests[request_id].get('response') is None:
                time.sleep(0.1)
            if request_id in self.pending_requests:
                result = self.pending_requests[request_id]['response']
                del self.pending_requests[request_id]
                return result
            else:
                return None
        else:
            if self.debug_mode:
                print("Control connection not established")
            return None

    def call_method(self, method: str, args: list = None, kwargs: dict = None):
        if self.control_ws:
            request_id = self._generate_request_id()
            self.pending_requests[request_id] = {'type': 'call', 'method': method, 'args': args, 'kwargs': kwargs}
            message = {
                'type': 'request',
                'id': request_id,
                'command': 'call',
                'method': method,
                'args': args,
                'kwargs': kwargs
            }
            self.control_ws.send(json.dumps(message))
        else:
            if self.debug_mode:
                print("Control connection not established")

    def shutdown(self):
        if self.control_ws:
            self.control_ws.close()
        if self.data_ws:
            self.data_ws.close()
        self.is_running = False

    def _generate_request_id(self):
        with self.lock:
            self.request_counter += 1
            return self.request_counter

