class RpcClient(metaclass=abc.ABCMeta):
    def __init__(self):
        self._context = zmq.Context()
        self._socket_sub = self._context.socket(zmq.SUB)
        self._socket_sub.setsockopt(zmq.KEepalive, 1)
        self._socket_sub.setsockopt(zmq.LINGER, 0)
        self._active = False
        self._thread = None
        self._lock = threading.Lock()
        self._last_received_ping = time.time()

    def start(self, sub_address: str) -> None:
        self._socket_sub.connect(sub_address)
        self._active = True
        self._thread = threading.Thread(target=self.run)
        self._thread.start()

    def stop(self) -> None:
        self._active = False
        if self._thread is not None:
            self._thread.join()

    def join(self) -> None:
        if self._thread is not None:
            self._thread.join()

    def run(self) -> None:
        while self._active:
            try:
                message = self._socket_sub.recv()
                topic_part, data_part = message.split(b':', 1)
                topic = topic_part.decode()
                data = data_part.decode()
                self.callback(topic, data)
                self._last_received_ping = time.time()
            except zmq.Again:
                if time.time() - self._last_received_ping > 5:
                    self.on_disconnected()
                    break
            except Exception as e:
                self.on_disconnected()
                break

    @abc.abstractmethod
    def callback(self, topic: str, data: Any) -> None:
        pass

    def subscribe_topic(self, topic: str) -> None:
        self._socket_sub.setsockopt(zmq.SUBSCRIBE, topic.encode())

    def on_disconnected(self) -> None:
        print("Warning: Connection to publisher lost.")

