        message = self.channel.encode('utf-8') + b'\x00' + serialized_data
        self.sink.send(message)

    def zmq_listen(self) -> Generator[bytes, None, None]:
        import zmq
        while True:
            message = self.sub.recv()
            yield message

    def _listen(self) -> Generator[Any, None, None]:
        import pickle
        for msg in self.zmq_listen():
            null_byte_index = msg.find(b'\x00')
            if null_byte_index == -1:
                continue
            channel = msg[:null_byte_index].decode('utf-8')
            data = msg[null_byte_index+1:]
            try:
                deserialized_data = pickle.loads(data)
            except Exception:
                continue
            if channel == self.channel:
                yield deserialized_data

