import numpy as np
import pickle
import time

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.receiver = self.context.socket(zmq.SUB)
        self.identity = identity or b'client'
        self.sender.setsockopt(zmq.IDENTITY, self.identity)
        self.sender.connect(f"tcp://{ip}:{port}")
        self.receiver.connect(f"tcp://{ip}:{port_out}")
        self.receiver.setsockopt(zmq.SUBSCRIBE, b'')
        self.request_id = 0
        self.pending_request = set()
        self.timeout = timeout

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, msg, msg_len=0):
        self.sender.send(msg)

    def _recv(self):
        try:
            return self.receiver.recv()
        except zmq.Again:
            return None

    def _recv_ndarray(self):
        raw_bytes = self._recv()
        if raw_bytes is None:
            return None
        try:
            data = pickle.loads(raw_bytes)
        except Exception as e:
            raise ValueError("Failed to decode numpy array") from e
        return data

    def encode(self, texts, blocking=True):
        request_id = self.request_id
        self.pending_request.add(request_id)
        self.request_id += 1
        self._send(texts.encode('utf-8'))
        if blocking:
            response = self._recv_ndarray()
            self.pending_request.discard(request_id)
            return response
        return None

    def fetch(self, delay=0.0):
        while True:
            if not self.pending_request:
                break
            time.sleep(delay)
            response = self._recv_ndarray()
            if response is not None:
                yield response
                self.pending_request.discard(response.request_id)

    def fetch_all(self, sort=True, concat=False):
        responses = []
        for resp in self.fetch():
            responses.append(resp)
        if sort:
            responses.sort(key=lambda x: x.request_id)
        if concat:
            return np.concatenate(responses)
        else:
            return responses

