class NetGear:
    def __init__(self, address=None, port=None, protocol=None, pattern=0, receive_mode=False, logging=False, **options):
        self.__logging = logging
        self.__msg_context = zmq.Context()
        self.__receive_mode = receive_mode
        self.__pattern = pattern
        self.__terminate = False
        self.__msg_socket = None
        self.__subscriber_timeout = 10
        self.__queue = deque()
        self.__thread = None

        if pattern == 2:
            if receive_mode:
                self.__msg_socket = self.__msg_context.socket(zmq.SUB)
                endpoint = f"{protocol}://{address}:{port}"
                self.__msg_socket.connect(endpoint)
                self.__msg_socket.setsockopt(zmq.RCVTIMEO, self.__subscriber_timeout * 1000)
            else:
                self.__msg_socket = self.__msg_context.socket(zmq.PUB)
                endpoint = f"{protocol}://{address}:{port}"
                self.__msg_socket.bind(endpoint)
        elif pattern == 0:
            self.__msg_socket = self.__msg_context.socket(zmq.PAIR)
            if receive_mode:
                endpoint = f"{protocol}://{address}:{port}"
                self.__msg_socket.connect(endpoint)
            else:
                self.__msg_socket.bind(endpoint)
        elif pattern == 1:
            if receive_mode:
                self.__msg_socket = self.__msg_context.socket(zmq.REP)
            else:
                self.__msg_socket = self.__msg_context.socket(zmq.REQ)
            endpoint = f"{protocol}://{address}:{port}"
            if receive_mode:
                self.__msg_socket.connect(endpoint)
            else:
                self.__msg_socket.bind(endpoint)
        else:
            raise ValueError("Invalid pattern")

        self.__compress = options.get('compress', False)
        if self.__compress:
            self.__compressor = zlib.compressobj(zlib.Z_DEFAULT_COMPRESSION)
            self.__decompressor = zlib.decompressobj()

        if receive_mode:
            self.__thread = threading.Thread(target=self.__recv_handler)
            self.__thread.start()

    def __recv_handler(self):
        while not self.__terminate:
            try:
                msg = self.__msg_socket.recv()
                if self.__compress:
                    decompressed = self.__decompressor.decompress(msg)
                    if self.__decompressor.flush(zlib.Z_FINISH) != zlib.Z_OK:
                        raise Exception("Decompression error")
                    msg = decompressed
                arr = np.frombuffer(msg, dtype=np.uint8)
                self.__queue.append(arr)
            except zmq.Again:
                pass
            except Exception as e:
                if self.__logging:
                    print(f"Error in recv_handler: {e}")
                self.__terminate = True

    def recv(self):
        if self.__queue:
            return self.__queue.popleft()
        return None

    def send(self, frame, message=None):
        if self.__compress:
            compressed = self.__compressor.compress(frame.tobytes())
            compressed += self.__compressor.flush(zlib.Z_FINISH)
        else:
            compressed = frame.tobytes()
        if message is not None:
            msg_bytes = pickle.dumps(message)
            combined = msg_bytes + compressed
            compressed = combined
        self.__msg_socket.send(compressed)

    def close(self, kill=False):
        if self.__msg_socket:
            self.__msg_socket.close()
        if self.__thread and self.__thread.is_alive():
            self.__terminate = True
            self.__thread.join()
        self.__msg_context.term()
        self.__queue.clear()

