            parts = message.split()
            if not parts:
                continue
            req_id = int(parts[0])
            req_objs = self.shm_req_manager.get_requests(req_id)
            multimodal_params = self.shm_req_manager.get_multimodal_params(req_id)
            start_time = asyncio.get_event_loop().time()
            if req_id not in self.req_id_to_out_inf:
                self.req_id_to_out_inf[req_id] = self.ReqStatus(req_id, multimodal_params, req_objs, start_time)
            status = self.req_id_to_out_inf[req_id]
            if status.can_release():
                self.recycle_event.set()

    async def recycle_resource_loop(self):
        while True:
            await self.recycle_event.wait()
            to_remove = []
            for req_id, status in list(self.req_id_to_out_inf.items()):
                if status.can_release():
                    self.shm_req_manager.release(req_id)
