            time.sleep(self._rpc_delay)
        if request.endpoint_name == self.NAME_FOR_ENDPOINT_UNKNOWN:
            context.set_code(grpc.StatusCode.INVALID_ARGUMENT)
            context.set_details('Unknown endpoint')
            return estop_pb2.RegisterEstopEndpointResponse(status=estop_pb2.RegisterEstopEndpointResponse.STATUS_UNKNOWN)
        elif request.endpoint_name == self.NAME_FOR_SERVER_ERROR:
            context.set_code(grpc.StatusCode.INTERNAL)
            context.set_details('Server error')
            return estop_pb2.RegisterEstopEndpointResponse(status=estop_pb2.RegisterEstopEndpointResponse.STATUS_SERVER_ERROR)
        else:
            return estop_pb2.RegisterEstopEndpointResponse(status=estop_pb2.RegisterEstopEndpointResponse.STATUS_SUCCESS)

