            payload_bytes = json.dumps(combined_payload).encode('utf-8')
            signature = self._encode_payload(payload_bytes, signature_secret)
            final_payload = {**combined_payload, 'signature': signature}
            headers = additional_headers.copy()
            async with httpx.AsyncClient(transport=transport) as client:
                response = await client.post(self.url, json=final_payload, headers=headers)
                response_json = response.json()
        except httpx.RequestError as e:
            return ExternalActionResponse(status=500, error="Network error occurred")
        except httpx.HTTPStatusError as e:
            return ExternalActionResponse(status=e.response.status_code, error=e.response.text)

