        return hmac.new(signature_secret.encode(), payload, hashlib.sha256).hexdigest()

    def _validate_response(self, response: Dict[str, Any]) -> ExternalActionResponse:
        if response.get('status') != 200:
            return ExternalActionResponse(status=response.get('status'), error="Invalid response status")
