import requests

class AccuWeatherClient:
    def __init__(self, api_key: str = "") -> None:
        self.api_key = api_key

    def get_weather(self, city: str, extensions: str = "all") -> str:
        if not self.api_key:
            return "Error: API key is required."

        search_url = f"https://api.accuweather.com/v1/location/search?apikey={self.api_key}&q={city}"
        response = requests.get(search_url)
        if response.status_code != 200:
            return f"Error: Could not find city '{city}'"

        data = response.json()
        if not data:
            return f"Error: Could not find city '{city}'"

        location_key = data[0]['Key']

        if extensions == "all":
            weather_url = f"https://api.accuweather.com/v1/currentconditions/v1?apikey={self.api_key}&locationkey={location_key}"
        elif extensions == "base":
            weather_url = f"https://api.accuweather.com/v1/forecasts/v1?apikey={self.api_key}&locationkey={location_key}"
        else:
            return "Error: Invalid extensions parameter. Use 'all' or 'base'."

        weather_response = requests.get(weather_url)
        if weather_response.status_code != 200:
            return f"Error: Could not retrieve weather data for '{city}'"

        weather_data = weather_response.json()

        if extensions == "all":
            current = weather_data[0]
            temp = current.get('Temperature', {}).get('Metric', {}).get('Value', 'N/A')
            humidity = current.get('RelativeHumidity', {}).get('Value', 'N/A')
            wind_speed = current.get('Wind', {}).get('Speed', {}).get('Metric', {}).get('Value', 'N/A')
            return f"Weather in {city}: {temp}°C, Humidity {humidity}%, Wind {wind_speed} m/s"
        else:
            forecasts = weather_data
            forecast_str = ""
            for i, forecast in enumerate(forecasts, 1):
                date = forecast.get('Date', 'N/A')
                temp_min = forecast.get('Temperature', {}).get('Minimum', {}).get('Metric', {}).get('Value', 'N/A')
                temp_max = forecast.get('Temperature', {}).get('Maximum', {}).get('Metric', {}).get('Value', 'N/A')
                forecast_str += f"Day {i}: {date}, Min {temp_min}°C, Max {temp_max}°C\n"
            return forecast_str

