import requests
from concurrent.futures import ThreadPoolExecutor

def query_dns_info(domain, record_type):
    def get_google_response():
        url = f"https://dns.google/resolve?name={domain}&type={record_type}"
        try:
            response = requests.get(url, timeout=5)
            if response.status_code == 200:
                return response.json()
            return None
        except requests.exceptions.RequestException:
            return None

    def get_cloudflare_response():
        url = "https://1.1.1.1/dns-query"
        payload = {"name": domain, "type": record_type}
        try:
            response = requests.post(url, json=payload, timeout=5)
            if response.status_code == 200:
                return response.json()
            return None
        except requests.exceptions.RequestException:
            return None

    with ThreadPoolExecutor() as executor:
        google_future = executor.submit(get_google_response)
        cloudflare_future = executor.submit(get_cloudflare_response)

        google_result = google_future.result()
        cloudflare_result = cloudflare_future.result()

    return [google_result, cloudflare_result]

