import requests

class AmazonSSO:
    def __init__(self, access_token: str = None, refresh_token: str = None, client_id: str, client_secret: str, user_pool_id: str, region: str):
        self.access_token = access_token
        self.refresh_token = refresh_token
        self.client_id = client_id
        self.client_secret = client_secret
        self.user_pool_id = user_pool_id
        self.region = region
        self.user_info = {}

    def get_new_token(self) -> str:
        token_url = f"https://{self.region}.cognito-idp.amazonaws.com/{self.user_pool_id}/oauth2/token"
        data = {
            'grant_type': 'refresh_token',
            'client_id': self.client_id,
            'client_secret': self.client_secret,
            'refresh_token': self.refresh_token
        }
        response = requests.post(token_url, data=data)
        if response.status_code == 200:
            self.access_token = response.json().get('access_token')
            return self.access_token
        return None

    def get_user_info(self) -> dict:
        openid_config_url = f"https://{self.region}.cognito-idp.amazonaws.com/{self.user_pool_id}/.well-known/openid-configuration"
        response = requests.get(openid_config_url)
        if response.status_code == 200:
            openid_config = response.json()
            userinfo_url = openid_config.get('userinfo_endpoint')
            headers = {'Authorization': f'Bearer {self.access_token}'}
            response = requests.get(userinfo_url, headers=headers)
            if response.status_code == 200:
                self.user_info = response.json()
                return self.user_info
        return {}

    @staticmethod
    def amazon_sso(code: str, redirect_uri: str = None, client_id: str, client_secret: str, user_pool_id: str, region: str) -> tuple["AmazonSSO", None] | tuple[None, None]:
        token_url = f"https://{region}.cognito-idp.amazonaws.com/{user_pool_id}/oauth2/token"
        data = {
            'grant_type': 'authorization_code',
            'code': code,
            'redirect_uri': redirect_uri,
            'client_id': client_id,
            'client_secret': client_secret
        }
        response = requests.post(token_url, data=data)
        if response.status_code == 200:
            tokens = response.json()
            access_token = tokens.get('access_token')
            refresh_token = tokens.get('refresh_token')
            instance = AmazonSSO(access_token=access_token, refresh_token=refresh_token, client_id=client_id, client_secret=client_secret, user_pool_id=user_pool_id, region=region)
            return (instance, None)
        return (None, None)

