def upload_image(image_tensor, api_key, service='smms'):
    if not isinstance(image_tensor, torch.Tensor):
        return None
    try:
        image = torchvision.transforms.ToPILImage()(image_tensor.clamp(0, 1))
        img_byte_arr = io.BytesIO()
        image.save(img_byte_arr, format='PNG')
        img_bytes = img_byte_arr.getvalue()
        base64_str = base64.b64encode(img_bytes).decode('utf-8')
    except Exception as e:
        return str(e)
    if service == 'smms':
        url = 'https://sm.ms/api/v2/upload'
        headers = {'Authorization': f'Bearer {api_key}'}
        payload = {'image': base64_str}
    elif service == 'imgbb':
        url = 'https://api.imgbb.com/1/upload'
        headers = {'Authorization': f'Client-ID {api_key}'}
        payload = {'image': base64_str}
    else:
        return "Invalid service specified"
    try:
        response = requests.post(url, headers=headers, data=payload)
        if response.status_code == 200:
            try:
                data = response.json()
                if service == 'smms':
                    if data.get('code') == 0:
                        return data['data']['url']
                    else:
                        return data.get('message', 'Upload failed')
                else:
                    if data.get('success') == True:
                        return data['data']['image_url']
                    else:
                        return data.get('message', 'Upload failed')
            except ValueError:
                return 'Invalid response'
        else:
            return response.text
    except requests.exceptions.RequestException as e:
        return str(e)
