import requests

def get_stock_data(symbol):
    url = f"https://query1.finance.yahoo.com/v7/finance/quote?symbols={symbol}"
    headers = {'User-Agent': 'Mozilla/5.0'}
    try:
        response = requests.get(url, headers=headers)
        response.raise_for_status()
    except requests.exceptions.RequestException as e:
        raise ValueError("HTTP request failed") from e
    data = response.json()
    quote_response = data.get('quoteResponse')
    if not quote_response or not quote_response.get('result'):
        raise ValueError("No data found for the symbol")
    stock_info = quote_response['result'][0]
    stock_data = {
        'price': stock_info.get('regularMarketPrice'),
        'volume': stock_info.get('regularMarketVolume'),
        'exchange': stock_info.get('exchange'),
        'shortName': stock_info.get('shortName'),
        'longName': stock_info.get('longName'),
        'quoteType': stock_info.get('quoteType')
    }
    return str(stock_data)

