        response.raise_for_status()
        task_id = response.json().get('task_id')
        if not task_id:
            raise Exception("Task ID not found in response")

        end_time = time.time() + timeout
        while time.time() < end_time:
            status_url = f"{self.base_url}/tasks/{task_id}"
            status_response = requests.get(status_url, headers=self.headers)
            status_response.raise_for_status()
            status = status_response.json().get('status')
            if status == 'completed':
                return status_response.json()
            elif status == 'failed':
                raise Exception("Task failed")
            time.sleep(5)

        raise TimeoutError("Task timeout exceeded")

    def submit_sync(self, request_data: dict):
        sync_url = f"{self.base_url}/sync_submit"
        response = requests.post(sync_url, json=request_data, headers=self.headers)
        response.raise_for_status()
