        self._app.post(
            "/v1/actions/run",
            summary="Execute action",
            response_model=ResponseBody,
        )(self.run_action)
        self._app.get(
            "/v1/actions/list",
            summary="List available actions",
        )(self.get_actions_list)

    async def run_action(self, body: RequestBody):
        result, status = await self._action_dispatcher.execute_action(
            body.action_name, body.action_parameters
        )
        return {"status": status, "result": result}

    async def get_actions_list(self):
        return self._action_dispatcher.get_registered_actions()

    def get_app(self):
        return self._app

