    def __init__(self, models_dirs=["saved_models", "base_models"]):
        self.app = Flask(__name__)
        CORS(self.app)
        self.api = Api(self.app, debug=True, validate=True, version="1.0", title="FARM NLP APIs")
        self.app.config["JSON_SORT_KEYS"] = True
        self.app.config["RESTPLUS_VALIDATE"] = True

        self.inferencers = {}
        self._load_models(models_dirs)
        self._setup_routes()

    def _load_models(self, models_dirs):
        model_paths = []
        for model_dir in models_dirs:
            path = Path(model_dir)
            if path.is_dir():
                models = [f for f in path.iterdir() if f.is_dir()]
                model_paths.extend(models)

        for idx, model_dir in enumerate(model_paths):
            self.inferencers[idx + 1] = Inferencer.load(str(model_dir), num_processes=0)

    def _setup_routes(self):
        @self.api.route("/models")
        class ModelListEndpoint(Resource):
            def get(self):
                resp = []
