class HTTPConnection(http.client.HTTPConnection):
    default_port = 80
    default_socket_options = [(socket.IPPROTO_TCP, socket.TCP_NODELAY, 1)]

    def __init__(self, host, port=None, *, timeout=None, source_address=None, blocksize=16384, socket_options=None, proxy=None, proxy_config=None):
        super().__init__(host, port, timeout=timeout, source_address=source_address)
        self.blocksize = blocksize
        self.socket_options = socket_options or self.default_socket_options
        self.proxy = proxy
        self.proxy_config = proxy_config
        self._has_connected_to_proxy = False
        self._response_options = None
        self._tunnel_host = None
        self._tunnel_port = None
        self._tunnel_scheme = None

    def _new_conn(self):
        try:
            sock = super()._new_conn()
            for opt in self.socket_options:
                sock.setsockopt(*opt)
            return sock
        except (socket.error, OSError) as e:
            raise http.client.HTTPException(f"Failed to create socket: {e}")

    def connect(self):
        if self.proxy:
            if not self._has_connected_to_proxy:
                proxy_host = self.proxy.hostname
                proxy_port = self.proxy.port or self.default_port
                proxy_addr = (proxy_host, proxy_port)
                sock = self._new_conn()
                try:
                    sock.connect(proxy_addr)
                    self.sock = sock
                    self._has_connected_to_proxy = True
                except (socket.error, OSError) as e:
                    raise http.client.HTTPException(f"Failed to connect to proxy: {e}")
        else:
            super().connect()

    def request(self, method, url, body=None, headers=None, *, chunked=False, preload_content=True, decode_content=True, enforce_content_length=True):
        if self.proxy and not self._has_connected_to_proxy:
            self.connect()
        try:
            super().request(method, url, body, headers, chunked=chunked, preload_content=preload_content, decode_content=decode_content, enforce_content_length=enforce_content_length)
        except Exception as e:
            self.close()
            raise

    def getresponse(self):
        return super().getresponse()

    def close(self):
        super().close()

