    def _serve_webui(self, file_name: str = "index.html") -> Response:
        static_dir = self.config.static_dir
        if not file_name:
            file_name = 'index.html'
        try:
            return send_from_directory(static_dir, file_name)
        except FileNotFoundError:
            return send_from_directory(static_dir, 'index.html')

    def _run(self):
        self.wsgiserver = WSGIServer((self.config.host, self.config.port), self.flask_app)
        while not self.stop_event.is_set():
            gevent.sleep(0.1)
        self.wsgiserver.shutdown()

    def start(self):
        gevent.spawn(self._run)

    def stop(self):
        if hasattr(self, 'wsgiserver'):
            self.stop_event.set()

    def unhandled_exception(self, exception: Exception) -> Response:
        logging.error("Unhandled exception: %s", exception)
        return "Internal Server Error", 500

